{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "$id": "https://json.schemastore.org/tombi.json",
  "title": "Tombi",
  "description": "**Tombi** (鳶) is a toolkit for TOML; providing a formatter/linter and language server.\n See the [GitHub repository](https://github.com/tombi-toml/tombi) for more information.",
  "type": "object",
  "properties": {
    "format": {
      "title": "Formatter options.",
      "anyOf": [
        {
          "$ref": "#/definitions/FormatOptions"
        },
        {
          "type": "null"
        }
      ]
    },
    "toml-version": {
      "title": "TOML version.",
      "description": "TOML version to use if not specified in the schema.",
      "anyOf": [
        {
          "$ref": "#/definitions/TomlVersion"
        },
        {
          "type": "null"
        }
      ],
      "default": "v1.0.0"
    },
    "include": {
      "title": "File patterns to include.",
      "description": "The file match pattern to include in formatting and linting.\n Supports glob pattern.",
      "type": ["array", "null"],
      "items": {
        "type": "string"
      },
      "minItems": 1
    },
    "exclude": {
      "title": "File patterns to exclude.",
      "description": "The file match pattern to exclude from formatting and linting.\n Supports glob pattern.",
      "type": ["array", "null"],
      "items": {
        "type": "string"
      },
      "minItems": 1
    },
    "lint": {
      "title": "Linter options.",
      "anyOf": [
        {
          "$ref": "#/definitions/LintOptions"
        },
        {
          "type": "null"
        }
      ]
    },
    "server": {
      "title": "Language server options.",
      "anyOf": [
        {
          "$ref": "#/definitions/ServerOptions"
        },
        {
          "type": "null"
        }
      ]
    },
    "schema": {
      "title": "Schema options.",
      "anyOf": [
        {
          "$ref": "#/definitions/SchemaOptions"
        },
        {
          "type": "null"
        }
      ]
    },
    "schemas": {
      "title": "Schema catalog items.",
      "type": ["array", "null"],
      "items": {
        "$ref": "#/definitions/Schema"
      }
    }
  },
  "additionalProperties": false,
  "x-tombi-toml-version": "v1.0.0",
  "x-tombi-table-keys-order": "schema",
  "definitions": {
    "TomlVersion": {
      "title": "TOML version.",
      "type": "string",
      "enum": ["v1.0.0", "v1.1.0-preview"]
    },
    "FormatOptions": {
      "title": "Formatter options.",
      "description": "To avoid needless discussion of formatting rules,\n we do not currently have a configuration item for formatting.",
      "type": "object",
      "additionalProperties": false,
      "x-tombi-table-keys-order": "schema"
    },
    "LintOptions": {
      "type": "object",
      "additionalProperties": false,
      "x-tombi-table-keys-order": "schema"
    },
    "ServerOptions": {
      "title": "Language Server options.",
      "type": "object",
      "properties": {
        "hover": {
          "title": "Hover Feature options.",
          "anyOf": [
            {
              "$ref": "#/definitions/ServerHover"
            },
            {
              "type": "null"
            }
          ]
        },
        "completion": {
          "title": "Completion Feature options.",
          "anyOf": [
            {
              "$ref": "#/definitions/ServerCompletion"
            },
            {
              "type": "null"
            }
          ]
        },
        "formatting": {
          "title": "Formatting Feature options.",
          "anyOf": [
            {
              "$ref": "#/definitions/ServerFormatting"
            },
            {
              "type": "null"
            }
          ]
        },
        "diagnostics": {
          "title": "Diagnostics Feature options.",
          "anyOf": [
            {
              "$ref": "#/definitions/ServerDiagnostics"
            },
            {
              "type": "null"
            }
          ]
        }
      },
      "additionalProperties": false,
      "x-tombi-table-keys-order": "schema"
    },
    "ServerHover": {
      "type": "object",
      "properties": {
        "enabled": {
          "title": "Enable hover feature.",
          "description": "Whether to enable hover.",
          "anyOf": [
            {
              "$ref": "#/definitions/BoolDefaultTrue"
            },
            {
              "type": "null"
            }
          ]
        }
      },
      "additionalProperties": false
    },
    "BoolDefaultTrue": {
      "type": "boolean",
      "default": true
    },
    "ServerCompletion": {
      "type": "object",
      "properties": {
        "enabled": {
          "title": "Enable completion feature.",
          "description": "Whether to enable completion.\n\n **WARNING**: 🚧 This feature is experimental 🚧",
          "anyOf": [
            {
              "$ref": "#/definitions/BoolDefaultTrue"
            },
            {
              "type": "null"
            }
          ]
        }
      },
      "additionalProperties": false
    },
    "ServerFormatting": {
      "type": "object",
      "properties": {
        "enabled": {
          "title": "Enable formatting feature.",
          "description": "Whether to enable formatting.",
          "anyOf": [
            {
              "$ref": "#/definitions/BoolDefaultTrue"
            },
            {
              "type": "null"
            }
          ]
        }
      },
      "additionalProperties": false
    },
    "ServerDiagnostics": {
      "type": "object",
      "properties": {
        "enabled": {
          "title": "Enable diagnostics feature.",
          "description": "Whether to enable diagnostics.",
          "anyOf": [
            {
              "$ref": "#/definitions/BoolDefaultTrue"
            },
            {
              "type": "null"
            }
          ]
        }
      },
      "additionalProperties": false
    },
    "SchemaOptions": {
      "type": "object",
      "properties": {
        "enabled": {
          "title": "Enable or disable the schema.",
          "anyOf": [
            {
              "$ref": "#/definitions/BoolDefaultTrue"
            },
            {
              "type": "null"
            }
          ]
        },
        "strict": {
          "title": "Enable or disable strict schema validation.",
          "description": "If `additionalProperties` is not specified in the JSON Schema,\n the strict mode treats it as `additionalProperties: false`,\n which is different from the JSON Schema specification.",
          "anyOf": [
            {
              "$ref": "#/definitions/BoolDefaultTrue"
            },
            {
              "type": "null"
            }
          ]
        },
        "catalog": {
          "title": "Schema catalog options.",
          "anyOf": [
            {
              "$ref": "#/definitions/SchemaCatalog"
            },
            {
              "type": "null"
            }
          ]
        }
      },
      "additionalProperties": false,
      "x-tombi-table-keys-order": "schema"
    },
    "SchemaCatalog": {
      "type": "object",
      "properties": {
        "path": {
          "title": "The schema catalog path or url.",
          "description": "The catalog is evaluated after the schemas specified by [[schemas]].\n\n You can specify multiple catalogs by making it an array.\n If you specify an array, the catalogs are searched in order of priority starting from the first catalog.\n If you want to disable the default catalog, specify an empty array.",
          "anyOf": [
            {
              "$ref": "#/definitions/OneOrMany_for_SchemaCatalogPath"
            },
            {
              "type": "null"
            }
          ],
          "default": "https://www.schemastore.org/api/json/catalog.json"
        }
      },
      "additionalProperties": false
    },
    "OneOrMany_for_SchemaCatalogPath": {
      "anyOf": [
        {
          "$ref": "#/definitions/SchemaCatalogPath"
        },
        {
          "type": "array",
          "items": {
            "$ref": "#/definitions/SchemaCatalogPath"
          }
        }
      ]
    },
    "SchemaCatalogPath": {
      "description": "Generic value that can be either single or multiple",
      "type": "string"
    },
    "Schema": {
      "anyOf": [
        {
          "$ref": "#/definitions/RootSchema"
        },
        {
          "$ref": "#/definitions/SubSchema"
        }
      ]
    },
    "RootSchema": {
      "title": "The schema for the root table.",
      "type": "object",
      "properties": {
        "toml-version": {
          "title": "The TOML version that the schema is available.",
          "anyOf": [
            {
              "$ref": "#/definitions/TomlVersion"
            },
            {
              "type": "null"
            }
          ]
        },
        "path": {
          "title": "The schema path.",
          "type": "string"
        },
        "include": {
          "title": "The file match pattern of the schema.",
          "description": "The file match pattern to include the target to apply the schema.\n Supports glob pattern.",
          "type": "array",
          "items": {
            "type": "string"
          },
          "minItems": 1
        }
      },
      "additionalProperties": false,
      "required": ["path", "include"],
      "x-tombi-table-keys-order": "schema"
    },
    "SubSchema": {
      "title": "The schema for the sub value.",
      "type": "object",
      "properties": {
        "path": {
          "title": "The sub schema path.",
          "type": "string"
        },
        "include": {
          "title": "The file match pattern of the sub schema.",
          "description": "The file match pattern to include the target to apply the sub schema.\n Supports glob pattern.",
          "type": "array",
          "items": {
            "type": "string"
          },
          "minItems": 1
        },
        "root-keys": {
          "title": "The keys to apply the sub schema.",
          "type": ["string", "null"],
          "minLength": 1
        }
      },
      "additionalProperties": false,
      "required": ["path", "include"],
      "x-tombi-table-keys-order": "schema"
    }
  }
}

{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "$id": "https://json.schemastore.org/maturin.json",
  "title": "ToolMaturin",
  "description": "The `[tool.maturin]` section of a pyproject.toml",
  "type": "object",
  "x-tombi-table-keys-order": "schema",
  "properties": {
    "all-features": {
      "description": "Activate all available features",
      "type": ["boolean", "null"]
    },
    "auditwheel": {
      "description": "Audit wheel mode",
      "anyOf": [
        {
          "$ref": "#/definitions/AuditWheelMode"
        },
        {
          "type": "null"
        }
      ]
    },
    "bindings": {
      "description": "Bindings type",
      "type": ["string", "null"]
    },
    "compatibility": {
      "description": "Platform compatibility",
      "anyOf": [
        {
          "$ref": "#/definitions/PlatformTag"
        },
        {
          "type": "null"
        }
      ]
    },
    "config": {
      "description": "Override a configuration value (unstable)",
      "type": ["array", "null"],
      "items": {
        "type": "string"
      }
    },
    "data": {
      "description": "Path to the wheel directory, defaults to `<module_name>.data`",
      "type": ["string", "null"]
    },
    "exclude": {
      "description": "Exclude files matching the given glob pattern(s)",
      "type": ["array", "null"],
      "items": {
        "$ref": "#/definitions/GlobPattern"
      }
    },
    "features": {
      "description": "Space or comma separated list of features to activate",
      "type": ["array", "null"],
      "items": {
        "type": "string"
      }
    },
    "frozen": {
      "description": "Require Cargo.lock and cache are up to date",
      "type": ["boolean", "null"]
    },
    "include": {
      "description": "Include files matching the given glob pattern(s)",
      "type": ["array", "null"],
      "items": {
        "$ref": "#/definitions/GlobPattern"
      }
    },
    "locked": {
      "description": "Require Cargo.lock is up to date",
      "type": ["boolean", "null"]
    },
    "manifest-path": {
      "description": "Path to Cargo.toml",
      "type": ["string", "null"]
    },
    "module-name": {
      "description": "Module name, accepts setuptools style import name like `foo.bar`",
      "type": ["string", "null"]
    },
    "no-default-features": {
      "description": "Do not activate the `default` feature",
      "type": ["boolean", "null"]
    },
    "profile": {
      "description": "Build artifacts with the specified Cargo profile",
      "type": ["string", "null"]
    },
    "python-packages": {
      "description": "Python packages to include",
      "type": ["array", "null"],
      "items": {
        "type": "string"
      }
    },
    "python-source": {
      "description": "The directory with python module, contains `<module_name>/__init__.py`",
      "type": ["string", "null"]
    },
    "rustc-args": {
      "description": "Additional rustc arguments",
      "type": ["array", "null"],
      "items": {
        "type": "string"
      }
    },
    "sdist-generator": {
      "description": "Source distribution generator",
      "default": "cargo",
      "allOf": [
        {
          "$ref": "#/definitions/SdistGenerator"
        }
      ]
    },
    "skip-auditwheel": {
      "description": "Skip audit wheel",
      "default": false,
      "type": "boolean"
    },
    "strip": {
      "description": "Strip the final binary",
      "default": false,
      "type": "boolean"
    },
    "target": {
      "description": "Target configuration",
      "default": {},
      "type": "object",
      "additionalProperties": {
        "$ref": "#/definitions/TargetConfig"
      }
    },
    "targets": {
      "description": "Cargo compile targets",
      "type": ["array", "null"],
      "items": {
        "$ref": "#/definitions/CargoTarget"
      }
    },
    "unstable-flags": {
      "description": "Unstable (nightly-only) flags to Cargo, see 'cargo -Z help' for details",
      "type": ["array", "null"],
      "items": {
        "type": "string"
      }
    }
  },
  "definitions": {
    "AuditWheelMode": {
      "description": "Auditwheel mode",
      "oneOf": [
        {
          "description": "Audit and repair wheel for manylinux compliance",
          "type": "string",
          "enum": ["repair"]
        },
        {
          "description": "Check wheel for manylinux compliance, but do not repair",
          "type": "string",
          "enum": ["check"]
        },
        {
          "description": "Don't check for manylinux compliance",
          "type": "string",
          "enum": ["skip"]
        }
      ]
    },
    "CargoCrateType": {
      "description": "Supported cargo crate types",
      "oneOf": [
        {
          "description": "Binary executable target",
          "type": "string",
          "enum": ["bin"]
        },
        {
          "description": "Dynamic system library target",
          "type": "string",
          "enum": ["cdylib"]
        },
        {
          "description": "Dynamic Rust library target",
          "type": "string",
          "enum": ["dylib"]
        },
        {
          "description": "Rust library",
          "type": "string",
          "enum": ["lib"]
        },
        {
          "description": "Rust library for use as an intermediate target",
          "type": "string",
          "enum": ["rlib"]
        },
        {
          "description": "Static library",
          "type": "string",
          "enum": ["staticlib"]
        }
      ]
    },
    "CargoTarget": {
      "description": "Cargo compile target",
      "type": "object",
      "required": ["name"],
      "x-tombi-table-keys-order": "schema",
      "properties": {
        "kind": {
          "description": "Kind of target (\"bin\", \"cdylib\")",
          "anyOf": [
            {
              "$ref": "#/definitions/CargoCrateType"
            },
            {
              "type": "null"
            }
          ]
        },
        "name": {
          "description": "Name as given in the `Cargo.toml` or generated from the file name",
          "type": "string"
        }
      }
    },
    "Format": {
      "description": "The target format for the include or exclude [GlobPattern].\n\nSee [Formats].",
      "oneOf": [
        {
          "description": "Source distribution",
          "type": "string",
          "enum": ["sdist"]
        },
        {
          "description": "Wheel",
          "type": "string",
          "enum": ["wheel"]
        }
      ]
    },
    "Formats": {
      "description": "A single [Format] or multiple [Format] values for a [GlobPattern].",
      "anyOf": [
        {
          "description": "A single [Format] value",
          "allOf": [
            {
              "$ref": "#/definitions/Format"
            }
          ]
        },
        {
          "description": "Multiple [Format] values",
          "type": "array",
          "items": {
            "$ref": "#/definitions/Format"
          }
        }
      ]
    },
    "GlobPattern": {
      "description": "A glob pattern for the include and exclude configuration.\n\nSee [PyProjectToml::include] and [PyProject::exclude].\n\nBased on <https://python-poetry.org/docs/pyproject/#include-and-exclude>.",
      "anyOf": [
        {
          "description": "A glob",
          "type": "string"
        },
        {
          "description": "A glob `path` with a `format` key to specify one or more [Format] values",
          "type": "object",
          "required": ["format", "path"],
          "x-tombi-table-keys-order": "schema",
          "properties": {
            "format": {
              "description": "One or more [Format] values",
              "allOf": [
                {
                  "$ref": "#/definitions/Formats"
                }
              ]
            },
            "path": {
              "description": "A glob",
              "type": "string"
            }
          }
        }
      ]
    },
    "PlatformTag": {
      "description": "Decides how to handle manylinux and musllinux compliance",
      "oneOf": [
        {
          "description": "Use the manylinux_x_y tag",
          "type": "object",
          "required": ["Manylinux"],
          "x-tombi-table-keys-order": "schema",
          "properties": {
            "Manylinux": {
              "type": "object",
              "required": ["x", "y"],
              "x-tombi-table-keys-order": "schema",
              "properties": {
                "x": {
                  "description": "GLIBC version major",
                  "type": "integer",
                  "format": "uint16",
                  "minimum": 0.0
                },
                "y": {
                  "description": "GLIBC version minor",
                  "type": "integer",
                  "format": "uint16",
                  "minimum": 0.0
                }
              }
            }
          },
          "additionalProperties": false
        },
        {
          "description": "Use the musllinux_x_y tag",
          "type": "object",
          "required": ["Musllinux"],
          "x-tombi-table-keys-order": "schema",
          "properties": {
            "Musllinux": {
              "type": "object",
              "required": ["x", "y"],
              "x-tombi-table-keys-order": "schema",
              "properties": {
                "x": {
                  "description": "musl libc version major",
                  "type": "integer",
                  "format": "uint16",
                  "minimum": 0.0
